import SidebarListItem from './SidebarListItem';
import SettingsIcon from './icons/SettingsIcon';
import SidebarIcon from './icons/SidebarIcon';
import ProfileIcon from './icons/ProfileIcon';
import NewIcon from './icons/NewIcon';
import BookIcon from './icons/BookIcon';
const Sidebar = ({selectedBook}) => {
    const [isCollapsed, setIsCollapsed] = useState(false); // State to manage sidebar collapse

  const toggleSidebar = () => {
    const [isCollapsed, setIsCollapsed] = useState(false); // State to manage sidebar collapse

    const toggleSidebar = () => {
        setIsCollapsed(!isCollapsed);
    }
}
    return (
      <div className="w-64 bg-gray-100 flex flex-col">
        <div className="p-4 flex justify-between">
            <a href="#" className="flex items-center gap-2 text-sm">
                <span className="py-1 px-1 rounded-3xl  bg-gray-200 hover:bg-gray-300">
                    <NewIcon size={24} strokeWidth={1.5} />
                </span>
            </a>
            <a href="#" onClick={toggleSidebar} className="flex items-center gap-2 text-sm">
                <span className="py-1 px-1 rounded-3xl hover:bg-gray-300">
                    <SidebarIcon className="-translate-x-0.5" size={24} strokeWidth={1.6} />
                </span>
            </a>
        </div>
        <nav className="flex flex-col p-4 grow"> 
          <ul className="space-y-4 border-b border-gray-300 pb-4">
          <SidebarListItem
            icon={SettingsIcon}
            label="Library"
            href="#"
          />
          <SidebarListItem
            icon={ProfileIcon}
            label="Profile"
            href="#"
          />
          <SidebarListItem
            icon={SettingsIcon}
            label="Settings"
            href="#"
          />
          </ul>
          <ul className="space-y-4 grow pt-4">
            {selectedBook ? (
                <SidebarListItem
                    icon={BookIcon}
                    label={`${selectedBook.title} — ${selectedBook.author}`}
                    href="#"
                    highlight={true}
                />
                ) : (
                <SidebarListItem
                    icon={BookIcon}
                    label="New Book"
                    href="#"
                />
                )}
          </ul>
            <div className="pt-4 bg-gray-100">
                <p className="text-xs">&copy; 2024  Delphi Demo.</p>
            </div>
        </nav>
      </div>
    );
  };

  export default Sidebar;
