import { useState } from 'react';
import './App.css';
import Sidebar from './components/Sidebar';
import Chat from './components/Chat';
import BookSelection from './components/BookSelection';
import FadeTransition from './components/FadeTransition'; // Import the transition component

function App() {
  const [selectedBook, setSelectedBook] = useState(null); // Track selected book

  const handleSelectBook = (book) => {
    setSelectedBook(book); // Store the selected book and switch view
  };

  return (
    <div className="App">
      <div>
        <Sidebar />
        <FadeTransition duration={500} className="flex h-dvh">
          {!selectedBook ? (
            <BookSelection onSelectBook={handleSelectBook} />
          ) : (
            <Chat selectedBook={selectedBook} />
          )}
        </FadeTransition>
      </div>
    </div>
  );
}

export default App;
