const books = [
    { id: 1, author: 'Leo Tolstoy', title: 'War and Peace', image: 'path/to/book1.jpg' },
    { id: 2, author: 'George Orwell', title: 'Animal Farm', image: '' },
    { id: 3, author: 'Fyodor Dostoevsky', title: 'The Brother Kramazov', image: '' },
  ];

  const BookSelection = ({ onSelectBook }) => {
    return (
      <div className="p-6 text-center grow">
        <h2 className="text-2xl mb-4">Which book would you like to study today?</h2>
        <div className="grid grid-cols-1 sm:grid-cols-3 gap-6">
          {books.map((book) => (
            <div key={book.id} className="cursor-pointer" onClick={() => onSelectBook(book)}>
              <img
                src={book.image}
                alt={book.title}
                className="aspect-portrait object-cover rounded-lg shadow-md"
              />
              <p className="mt-2 text-lg">{book.title}</p>
            </div>
          ))}
        </div>
      </div>
    );
  };

  export default BookSelection;
