const books = [
    { id: 1, author: 'Leo Tolstoy', title: 'War and Peace', image: 'path/to/book1.jpg' },
    { id: 2, author: 'George Orwell', title: 'Animal Farm', image: '' },
    { id: 3, author: 'Albert Camus', title: "L'Étranger", image: '' },
  ];

  const BookSelection = ({ onSelectBook }) => {
    return (
      <div className="p-6 text-center grow flex flex-col justify-center items-center">
        <h2 className="text-2xl mb-4">Which book would you like to study today?</h2>
        <div className="grid grid-cols-1 sm:grid-cols-3 gap-6 max-w-126 ">
          {books.map((book) => (
            <div key={book.id} className="cursor-pointer bg-emerald-500 w-32 h-48 flex items-center justify-center drop-shadow-2xl shadow-inner" onClick={() => onSelectBook(book)}>
              <h4 className="text-lg p-2 bg-yellow-50 font-bold w-full h-24 text-sm flex items-center">{book.title}<br />—<br />{book.author}</h4>
            </div>
          ))}
        </div>
      </div>
    );
  };

  export default BookSelection;
