import { useState, useEffect } from 'react'

const useTypewriter = (text, speed = 50) => {
  const [displayedText, setDisplayedText] = useState("")

  useEffect(() => {
    // Reset displayedText every time text changes
    setDisplayedText("")
    let index = 0

    const interval = setInterval(() => {
      if (index < text.length) {
        setDisplayedText((prev) => prev + text.charAt(index)) // Add character by character
        index += 1
      } else {
        clearInterval(interval) // Clear interval after finishing typing
      }
    }, speed)

    return () => clearInterval(interval) // Clear interval on unmount or when text changes
  }, [text, speed])

  return displayedText
}

export default useTypewriter
