import { useState } from 'react';

function SignUp() {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const response = await fetch('http://localhost:5000/auth/register', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(formData),
    });

    const data = await response.json();
    if (response.ok) {
      alert('User registered successfully');
    } else {
      alert(`Error: ${data.message}`);
    }
  };

  return (
    <div className="h-dvh w-vw flex justify-center items-center">
      <form className="w-1/5 flex flex-col justify-center items-center space-y-2"onSubmit={handleSubmit}>
        <input className="w-full rounded-3xl p-2 focus:outline-none focus:ring-2 focus:ring-indigo-500" type="email" name="email" value={formData.email} onChange={handleChange} placeholder="Email" />
        <input className="w-full rounded-3xl p-2 focus:outline-none focus:ring-2 focus:ring-indigo-500" type="password" name="password" value={formData.password} onChange={handleChange} placeholder="Password" />
        <button className="w-full rounded-3xl px-4 py-2 bg-indigo-500 text-white hover:bg-indigo-60" type="submit">Login</button>
      </form>
    </div>
  );
}

export default SignUp;
