import { useState } from 'react';
import Message from './Message';
import MessageInput from './MessageInput';

const Chat = ({ selectedBook }) => {
  const initMessage = `Hi, I am Delphi. What would you like to know about ${selectedBook.title}?`;

  const [messages, setMessages] = useState([
    { text: initMessage, sender: "bot" }
  ]);

  const addMessage = (message) => {
    setMessages([...messages, { text: message, sender: "user" }]);
    setTimeout(() => {
      setMessages((prevMessages) => [
        ...prevMessages,
        { text: "Let me check that for you...", sender: "bot" }
      ]);
    }, 1000);
  };

  return (
    <div className="flex flex-col grow h-dvh mx-auto">
      <div className="flex-1 overflow-y-auto bg-gray-10 p-4">
          {messages.map((msg, index) => (
          <Message key={index} text={msg.text} sender={msg.sender} />
          ))}
      </div>

      <MessageInput onSend={addMessage} />
    </div>
  );
};

export default Chat;
