import { useState } from 'react'
import Sidebar from '../components/Sidebar'
import Chat from '../components/Chat'
import BookSelection from '../components/BookSelection'
import Profile from '../components/Profile'
import Dashboard from '../components/Dashboard' // Import Dashboard component

const MainView = () => {
  const [selectedBook, setSelectedBook] = useState(null)
  const [activeView, setActiveView] = useState('dashboard') // Set initial view to Dashboard
  const [selectedChatId, setSelectedChatId] = useState(null)

  const handleSelectBook = (book) => {
    setSelectedBook(book)
    setActiveView('chat')
  }

  const handleSelectChat = (chatId) => {
    setSelectedChatId(chatId)
    setActiveView('chat')
  }

  const handleNewChat = () => {
    setActiveView('bookSelection') // Navigate to BookSelection on New Chat
  }

  return (
    <div className="flex h-dvh">
      <Sidebar
        selectedBook={selectedBook}
        setActiveView={setActiveView}
        onSelectChat={handleSelectChat}
        onNewChat={handleNewChat} // Pass the new handler to Sidebar
      />
      {activeView === 'dashboard' ? (
        <Dashboard />
      ) : activeView === 'bookSelection' ? (
        <BookSelection onSelectBook={handleSelectBook} />
      ) : activeView === 'chat' ? (
        selectedBook ? (
          <Chat selectedBook={selectedBook} chatId={selectedChatId} />
        ) : (
          <BookSelection onSelectBook={handleSelectBook} />
        )
      ) : activeView === 'profile' ? (
        <Profile />
      ) : (
        <div>Unknown view</div>
      )}
    </div>
  )
}

export default MainView
