import { useState, useEffect } from 'react'

const useTypewriter = (text, speed = 300) => {
  const [displayedText, setDisplayedText] = useState("")

  useEffect(() => {
    setDisplayedText("")
    const words = text.split(" ")
    let index = 0

    words.forEach((word, i) => {
      setTimeout(() => {
        setDisplayedText((prev) => (prev ? `${prev} ${word}` : word))
      }, i * speed)
    })

    return () => {
      index = words.length
